--
-- Table structure for table `article`
--

CREATE TABLE `article` (
  `idArticle` int(11) NOT NULL PRIMARY KEY,
  `titre` char(50) NOT NULL,
  `tags` text,
  `idContenu` int(11) NOT NULL,
  `dateArticle` datetime NOT NULL
);

--
-- Table structure for table `commentaire`
--


CREATE TABLE `commentaire` (
  `idCommentaire` int(11) NOT NULL PRIMARY KEY,
  `dateComm` datetime NOT NULL,
  `commentateur` char(50) NOT NULL,
  `commentaire` text,
  `idArticle` int(11) NOT NULL
);

--
-- Table structure for table `contenuPage`
--
CREATE TABLE `contenuPage` (
  `idContenu` int(11) NOT NULL PRIMARY KEY,
  `contenu` text   NOT NULL
);



--
-- Table structure for table `fichiers`
--
CREATE TABLE `fichiers` (
  `idFic` int(11) NOT NULL PRIMARY KEY,
  `nom` char(50)   NOT NULL,
  `chemin` char(70)   NOT NULL
) ;


--
-- Table structure for table `lien`
--
CREATE TABLE `lien` (
  `idLien` int(11) NOT NULL PRIMARY KEY,
  `label` char(50)   NOT NULL,
  `lienParent` int(11) DEFAULT NULL,
  `idContenu` int(11) DEFAULT NULL,
  `tags` text  
) ;


--
-- Table structure for table `users`
--
CREATE TABLE `users` (
  `idUser` int(11) NOT NULL PRIMARY KEY,
  `nom` char(10)   NOT NULL,
  `mdp` char(50)   NOT NULL
);

